const btnModal=document.querySelectorAll("[data-modal]"),btnModalClose=document.querySelectorAll(".modal__close"),modal=document.querySelectorAll(".modal"),body=document.body;function openModal(e){let t=e.querySelector(".modal__content");e.classList.add("modal--active"),body.classList.add("no-scroll"),setTimeout(()=>{t.style.transform="none",t.style.opacity=1},1)}function closeModal(e){e.querySelector(".modal__content").removeAttribute("style"),setTimeout(()=>{e.classList.remove("modal--active"),body.classList.remove("no-scroll")},200)}btnModal.forEach(e=>{e.addEventListener("click",e=>{let t=e.currentTarget.getAttribute("data-modal"),o=document.getElementById(t);o.querySelector(".modal__content").addEventListener("click",e=>{e.stopPropagation()}),openModal(o)})}),btnModalClose.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget.closest(".modal"))})}),modal.forEach(e=>{e.addEventListener("click",e=>{closeModal(e.currentTarget)})});const burger=document.getElementById("sidebarToggle"),sidebar=document.getElementById("sidebar");function sidebarActive(){burger.classList.add("burger--active"),body.classList.add("sidebar--active"),sidebar.classList.add("sidebar--active")}function sidebarClose(){burger.classList.remove("burger--active"),body.classList.remove("sidebar--active"),sidebar.classList.remove("sidebar--active")}burger.addEventListener("click",e=>{body.classList.contains("sidebar--active")?sidebarClose():sidebarActive()});